#include <stdio.h>
#include <stdlib.h>
#define TRUE 1
#define FALSE 0
int diasNoMes[] = {0,31,28,31,30,31,30,31,31,30,31,30,31}; int verificaAnoBissexto(int a) { if ((a%4 == 0 && a%100 == 0 && a%400 == 0) || (a%4 == 0 && a%100 != 0) ) { diasNoMes[2] = 29; return TRUE;} if (a%4 !=0 || (a%4 == 0 && a%100 == 0 && a%400 != 0)) return FALSE; return 0;} int verificaDataValida(int d, int m, int a) { if(m > 12 || m < 1) return FALSE;if(d < 1) return FALSE;if(a < 0) return FALSE;if ( d > diasNoMes[m] ) return FALSE;return TRUE;}int proximosEncontros(int d,int m, int a){int intervalo,n = 1;while  (!(n%2 == 0 && n%3 == 0 && n%4 == 0 && n%5 == 0 && n%6 == 0)) {n++; }intervalo = n;while (m < 12){d = d + intervalo;while (d > diasNoMes[m]) {d = d - diasNoMes[m] ;m++;if (m == 12 && d > diasNoMes[12]) break;}if (d < diasNoMes[m]) printf("%i/%i/%i\n", d, m, a);}return 0;}int main() {int dia, mes, ano;printf("Entre com a data de inicio do ano letivo:\n");printf("Entre com o dia: ");scanf("%d", &dia);printf("Entre com o mes: ");scanf("%d", &mes);printf("Entre com o ano: ");scanf("%d", &ano);verificaAnoBissexto(ano);if(verificaDataValida(dia, mes, ano) == FALSE){printf("Dados incorretos\n");exit(1);}printf("Proximos dias de encontro de todos os clubes:\n");proximosEncontros(dia, mes, ano);return 0;}